import requestsfrom time import sleepfrom random import randintdef request(num_operation, id_=0):    try:        if 1 <= num_operation <= 5:            if num_operation == 1:                # users request:                url = f'https://jsonplaceholder.typicode.com/users'                response = requests.get(url)                result_request = response.json()                return result_request                # post request            elif num_operation == 2:                url = f'https://jsonplaceholder.typicode.com/posts?userId={id_}'                response = requests.get(url)                result_request = response.json()                return result_request                # to do request            elif num_operation == 3:                url = 'https://jsonplaceholder.typicode.com/todos'                response = requests.get(url)                result_request = response.json()                return result_request                # comments request            elif num_operation == 4:                url = f'https://jsonplaceholder.typicode.com/comments'                response = requests.get(url)                result_request = response.json()                return result_request                # picture request            elif num_operation == 5:                url = f'https://jsonplaceholder.typicode.com/photos/'                response = requests.get(url)                result_request = response.json()                return result_request    except requests.exceptions.Timeout:        print("Сервер не отвечает, попробуйте позже!")    except requests.exceptions.TooManyRedirects:        print("Вероятно некорректный URL")    except requests.ConnectionError:        print("Нет соединения с сервером")def identefication():    flag = True    while flag:        try:            result_request = request(1)            for value in result_request:                print(f"Id - {value['id']} | Name - {value['name']} | "                      f"Username - {value['username']}")                sleep(0.1)            user_id = int(input("\nEnter user id:\n"                                "Exit - 0:\n"))            if 1 <= user_id <= 10:                for value in result_request:                    if user_id == value['id']:                        main_menu(user_id)            elif user_id == 0:                flag = False            else:                print("Wrong user Id!\n")        except ValueError:            print("Wrong value!\n")def main_menu(id_):    flag = True    while flag:        try:            print(f"User information - 1\n"                  f"User post's - 2\n"                  f"TODO - 3\n"                  f"Random picture url - 4\n"                  f"Return - 0")            menu_item = abs(int(input("Choose menu item:\n")))            if 0 <= menu_item <= 4:                if menu_item == 1:                    userinfo(id_)                    continue                elif menu_item == 2:                    posts(id_)                    continue                elif menu_item == 3:                    todo(id_)                    continue                elif menu_item == 4:                    random_picture_url(id_)                    continue                elif menu_item == 0:                    flag = False            else:                print("Wrong menu item!\n")        except ValueError:            print("Wrong value!\n")        except TypeError:            print("Wrong type!\n")    returndef userinfo(id_):    result_request = request(1, id_)    for value in result_request:        if id_ == value['id']:            print(f"| Id - {value['id']} |\n"                  f"| Name - {value['name']} |\n"                  f"| Username - {value['username']} |\n"                  f"| Phone - {value['phone']} |\n"                  f"| Email - {value['email']} |\n"                  f"| Website - {value['website']} |\n"                  f"| Company: Name - {value['company']['name']} |\n"                  f"| CatchPhrase - {value['company']['catchPhrase']} |\n"                  f"| bs - {value['company']['bs']} |\n"                                    f"| Address: City - {value['address']['city']} | "                  f"Street - {value['address']['street']} | "                  f"Suite - {value['address']['suite']} | \n"                  f"| Zipcode - {value['address']['zipcode']} | "                  f"Geo: Lat - {value['address']['geo']['lat']}, "                  f"Lng - {value['address']['geo']['lng']}\n")def posts(id_):    flag = True    while flag:        try:            print(f"\nPost's list - 1\n"                  f"Post information - 2\n"                  f"Return - 0")            menu_item = abs(int(input("Choose post-menu item:\n")))            post_list = []            if 0 <= menu_item <= 2:                if menu_item == 1:                    result_request = request(2, id_)                    for value in result_request:                        if id_ == value['userId']:                            print(f"Post id - {value['id']} | "                                  f"Title - {value['title']}")                            sleep(0.1)                elif menu_item == 2:                    result_request = request(2, id_)                    for value in result_request:                        if id_ == value['userId']:                            print(f"| Post id - {value['id']} |")                            post_list.append(value['id'])                    post_id = abs(int(input("Choose post id:\n")))                    if post_id in post_list:                        comment_request = request(4, post_id)                        comment_list = []                        for value in result_request:                            if post_id == value['id']:                                print(f"Post id - {value['id']} | "                                      f"Post title - {value['title']}\n"                                      f"| Post body - {value['body']}")                                sleep(0.1)                        for val in comment_request:                            if post_id == val['postId']:                                comment_list.append(val['id'])                                print(f"| Comment id - {val['id']} |")                        print(f"| Number of comments - {len(comment_list)} |")                    else:                        print("Wrong value!")                elif menu_item == 0:                    flag = False            else:                print("Wrong post-menu item!")        except ValueError:            print("Wrong value!")        except TypeError:            print("Wrong type!")    returndef todo(id_):    flag = True    while flag:        try:            print(f"\nUncompleted tasks - 1\n"                  f"Completed tasks - 2\n"                  f"Return - 0")            menu_item = abs(int(input("Choose todo item:\n")))            if 0 <= menu_item <= 2:                if menu_item == 1:                    result_request = request(3, id_)                    for value in result_request:                        if id_ == value['userId'] and value['completed'] is False:                            print(f"User Id - {value['userId']} | "                                  f"Complete status - {value['completed']}\n"                                  f"TODO's id - {value['id']} | Title - {value['title']}\n")                            sleep(0.2)                elif menu_item == 2:                    result_request = request(3, id_)                    for value in result_request:                        if id_ == value['userId'] and value['completed'] is True:                            print(f"User Id - {value['userId']} | "                                  f"Complete status - {value['completed']}\n"                                  f"TODO's id - {value['id']} | Title - {value['title']}\n")                            sleep(0.2)                elif menu_item == 0:                    flag = False            else:                print("Wrong item!")        except ValueError:            print("Wrong value!")def random_picture_url(id_):    result_request = request(5, id_)    list_of_img_id = []    for value in result_request:        list_of_img_id.append(value['id'])    random_num = randint(min(list_of_img_id), max(list_of_img_id))    for value in result_request:        if random_num == value['id']:            print(f"Album id - {value['albumId']} | "                  f"Picture id - {value['id']} | "                  f"Picture url - {value['url']}\n")identefication()