class Person(object):    def __init__(self, age, name, sex):        self.age = int(age)        self.name = str(name)        self.sex = str(sex)    def show_age(self):        return self.age    def print_name(self):        return self.name    def show_all_info(self):        return self.__dict__person_1 = Person(31, 'Tolyan', 'man')person_2 = Person(29, 'Kolyan', 'man')person_1.profession = "builder"person_2.profession = "programmer"print(person_1.profession)print(person_2.profession)print(person_1.show_all_info())print(person_2.show_all_info())