class Figure(object):    color = 'white'    def change_color(self, new_color):        self.color = new_color        return self.colorclass Oval(Figure):    def __init__(self, radius):        self.radius = radius        self.r_width = round(radius * 1.22, 1)        self.r_length = round(radius * 0.71, 1)class Square(Figure):    def __init__(self, side):        self.side = side