class Figure(object):    def __init__(self, color):        self.color = color    def change_color(self, new_color):        self.color = new_colorclass Oval(Figure):    def __init__(self, color, radius):        super().__init__(color)        self.radius = radius        self.r_width = round(radius * 1.22, 1)        self.r_length = round(radius * 0.71, 1)class Square(Figure):    def __init__(self, color, side):        super().__init__(color)        self.side = side