class Vehicle(object):    count_instances = 0    def __init__(self, color, doors, tires, v_type):        Vehicle.count_instances += 1        self.color = color        self.doors = doors        self.tires = tires        self.v_type = v_type    @classmethod    def total_objects(cls):        print(f"Total objects is {cls.count_instances}")    def __del__(self):        Vehicle.count_instances -= 1