from django import formsfrom django.contrib.auth.forms import AuthenticationFormfrom django.forms import ModelForm, TextInput, ImageField, Textareafrom django.views.generic.edit import BaseDeleteViewfrom viewshop.models import Productclass LoginForm(AuthenticationForm):    username = forms.CharField()    password = forms.CharField(widget=forms.PasswordInput)class ProductForm(ModelForm):    class Meta:        model = Product        fields = ['title', 'category',                  'description', 'price',                  'quantity', 'product_img', 'active_status']        widgets = {            'title': TextInput(attrs={                "class": "form-control"            }),            'category': TextInput(attrs={                "class": "form-control"            }),            'description': Textarea(attrs={'cols': 60,                                           'rows': 10,                                           "class": "form-control"                                           }),            'price': TextInput(attrs={                "class": "form-control"            }),            'quantity': TextInput(attrs={                "class": "form-control"            }),        }class DeleteProduct(ModelForm):    class Meta:        model = Product        template_name = 'viewshop/product_confirm_delete.html'        fields = ['title', 'category',                  'description', 'price',                  'quantity', 'product_img', 'active_status']