class LenOrSymbolsException(Exception):    passclass OutOfRange(Exception):    passclass UnexpectedValue(Exception):    passdef some_symbols(file_name, num_symbols):    with open(f"{file_name}", "r", encoding="utf-8") as text_file:        content = text_file.read().strip('\n')        empty = content.replace("\\", "/")        len_string = len(empty)        mid = len_string // 2        mid_symbols = num_symbols // 2        m_step = mid - mid_symbols        p_step = mid + mid_symbols        out_list = []        try:            if (len_string % 2 == 0) and (num_symbols % 2 == 0):                if len_string >= 2 or len_string >= num_symbols:                    out_list.append(empty[0:num_symbols:])                    mid_symbol = (empty[m_step:mid] + empty[mid:p_step])                    out_list.append(mid_symbol)                    out_list.append(empty[len_string-num_symbols::])                    print(out_list)                elif (len_string % 2 == 0) and (num_symbols % 2 != 0):                    raise UnexpectedValue                else:                    raise OutOfRange            elif (len_string % 2 != 0) and (num_symbols % 2 != 0):                if len_string >= 3 or len_string >= num_symbols:                    out_list.append(empty[0:num_symbols:])                    mid = len_string // 2                    mid_symbol = (empty[m_step:mid] + empty[mid:p_step+1])                    out_list.append(mid_symbol)                    out_list.append(empty[len_string - num_symbols::])                    print(out_list)                elif (len_string % 3 == 0 ) and (num_symbols % 2 != 0):                    raise UnexpectedValue                else:                    raise OutOfRange            else:                raise LenOrSymbolsException        except LenOrSymbolsException:            print("WRONG STRING OR SYMBOLS")        except OutOfRange:            print("OUT OF RANGE")        except UnexpectedValue:            print("UnexpectedValue")number_symb = int(input("Enter number of symbols: \n"))some_symbols("test.data_4.txt", number_symb)'''FILE NAMES:test.data.txttest.data_1.txttest.data_2.txttest.data_3.txttest.data_4.txttest.data_5.txt'''